import React, { Component } from 'react'

import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'
import { Button } from 'react-bootstrap'
import Dropdown from 'react-bootstrap/DropdownButton'

import API from '../../../services/api'

class FormNovoProcesso extends Component {
  constructor(props) {
    super(props);
    this.state = {
      UFs: [],
      municipios: [],
      estado: '',
      selection: 0
    }
  }

  componentDidMount() {
    this.getUFs()
  }

  async getUFs() {
    const res = await API.get(`regiao/estados/siglas`)
    this.setState({ UFs: res.data })
  }

  selecionarEstado(e, uf) {
    if(!uf) {
      this.setState({ estado: '', municipios: [] })
      return false
    }
    this.setState({ estado: uf }, function() {
      this.getMunicipios()
    })
  }

  async getMunicipios() {
    const res = await API.get(`regiao/municipios/${this.state.estado}`)
    let municipios = res.data
    this.setState({ municipios: municipios })
  }

  render() {
    return (
      <Form>
      <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
          Dropdown Button
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
          <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
          <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
        <Form.Row>
          <Form.Group as={Col} controlId="formBasicEmail">
          </Form.Group>

          <Form.Group as={Col} controlId="formBasicEmail">
            <Form.Label>Comarca </Form.Label>
            <Form.Control as="select">
              {this.state.municipios.map((municipio, index) => {
                return (
                  <option key={index}>{municipio['nome']}</option>
                );
              })}
            </Form.Control>
          </Form.Group>

          <Form.Group as={Col} controlId="formBasicEmail">
            <Form.Label>Vara </Form.Label>
            <Form.Control type="vara" placeholder="" />
          </Form.Group>
        </Form.Row>

        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
            </Form.Text>
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>

        <Form.Group controlId="formBasicChecbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
          </Button>
      </Form>
    )
  }
}

export default FormNovoProcesso
